def image_colorizer(url):
    colorizer = get_image_colorizer(artistic=True)
    source_url = url
    render_factor = 35
    watermarked = True

    if source_url is not None and source_url !='':
        print(source_url)
        image_path = colorizer.get_transformed_image(path=source_url, render_factor=render_factor, watermarked=watermarked)

    else:
        print('Вставьте другую ссылку и попробуйте еще раз')
    return image_path


def load_img(img_file):
    img = Image.open(img_file)
    return img


st.title('Раскрашивание черно-белых фотографий')
st.header('Проект разработан учеником МАОУ СОШ 13 им. П. А. Леонова  10 Б класса Макаровым Артемием Ивановичем', divider='gray')
st.image(['gray_photo.jpg', 'my_photo.jpg'], width=350)
st.text('Пример использования веб-приложения')

with st.container(border=1):
    uploaded_file = st.file_uploader("Choose a file")

if uploaded_file is not None:
    if st.button('Раскрасить!'):

        col1, col2 = st.columns([1,1])
        file_bytes = np.asarray(bytearray(uploaded_file.read()), dtype=np.uint8)
        opencv_image = cv2.imdecode(file_bytes, 1)
        img = load_img(uploaded_file)

        with col1:
            st.info("Ваше изображение")
            st.image(uploaded_file)
        with col2:
            result = image_colorizer(uploaded_file)
            st.success("Ваше раскрашенное изображение")
            st.image(result)